mapper.select:
select 
from t_shipping
where id = #{shippingId} and customer_id = #{customerId}

select 
from t_shipping
where customer_id = #{customerId} and status = #{status}

mapper.update:
update t_shipping
    set status = #{status},
        update_time = #{updateTime}
where id = #{shippingId} and customer_id = #{customerId}
